import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { useAuth } from '../contexts/AuthContext';
import { Transaction } from '@e-wallet/shared';
import apiService from '../services/api';
import Card from '../components/Card';
import Button from '../components/Button';
import Input from '../components/Input';

// Page container
const TransactionsContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

// Page title
const PageTitle = styled.h1`
  font-size: 2rem;
  font-weight: 700;
  color: #1f2937;
  margin-bottom: 2rem;
`;

// Filters section
const FiltersSection = styled.div`
  margin-bottom: 2rem;
`;

const FiltersGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
  margin-bottom: 1rem;
`;

const FilterSelect = styled.select`
  padding: 0.5rem 0.75rem;
  font-size: 1rem;
  border-radius: 0.375rem;
  border: 1px solid #d1d5db;
  background-color: white;
  color: #1f2937;
  
  &:focus {
    outline: none;
    border-color: #3b82f6;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.2);
  }
`;

// Transactions list
const TransactionsList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const TransactionItem = styled(Card)`
  display: flex;
  justify-content: between;
  align-items: center;
  padding: 1rem;
`;

const TransactionInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  flex: 1;
`;

const TransactionIcon = styled.div<{ type: string }>`
  width: 3rem;
  height: 3rem;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.25rem;
  background-color: ${props => {
    switch (props.type) {
      case 'deposit':
        return '#dcfce7';
      case 'withdrawal':
        return '#fee2e2';
      case 'transfer':
        return '#dbeafe';
      default:
        return '#f3f4f6';
    }
  }};
  color: ${props => {
    switch (props.type) {
      case 'deposit':
        return '#16a34a';
      case 'withdrawal':
        return '#dc2626';
      case 'transfer':
        return '#2563eb';
      default:
        return '#6b7280';
    }
  }};
`;

const TransactionDetails = styled.div`
  flex: 1;
`;

const TransactionType = styled.div`
  font-weight: 600;
  color: #1f2937;
  margin-bottom: 0.25rem;
`;

const TransactionDescription = styled.div`
  color: #6b7280;
  font-size: 0.875rem;
`;

const TransactionAmount = styled.div<{ type: string }>`
  font-size: 1.25rem;
  font-weight: 700;
  color: ${props => {
    switch (props.type) {
      case 'deposit':
        return '#16a34a';
      case 'withdrawal':
        return '#dc2626';
      case 'transfer':
        return '#2563eb';
      default:
        return '#6b7280';
    }
  }};
`;

const TransactionDate = styled.div`
  color: #6b7280;
  font-size: 0.875rem;
  text-align: right;
`;

// Empty state
const EmptyState = styled.div`
  text-align: center;
  padding: 3rem 1rem;
  color: #6b7280;
`;

const EmptyStateIcon = styled.div`
  font-size: 4rem;
  margin-bottom: 1rem;
`;

const EmptyStateTitle = styled.h3`
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: #374151;
`;

const EmptyStateDescription = styled.p`
  margin-bottom: 2rem;
`;

// Mock transaction data (replace with API call)
const mockTransactions = [
  {
    id: '1',
    type: 'deposit',
    amount: 100.00,
    description: 'Bank deposit',
    date: '2025-06-08T10:30:00Z',
    status: 'completed'
  },
  {
    id: '2',
    type: 'transfer',
    amount: 25.50,
    description: 'Transfer to John Doe',
    date: '2025-06-07T15:45:00Z',
    status: 'completed'
  },
  {
    id: '3',
    type: 'withdrawal',
    amount: 50.00,
    description: 'ATM withdrawal',
    date: '2025-06-06T09:15:00Z',
    status: 'completed'
  }
];

const TransactionsPage: React.FC = () => {
  const { wallets } = useAuth();
  const [transactions, setTransactions] = useState(mockTransactions);
  const [selectedWallet, setSelectedWallet] = useState<string>('all');
  const [selectedType, setSelectedType] = useState<string>('all');
  const [dateFilter, setDateFilter] = useState<string>('all');

  const getTransactionIcon = (type: string) => {
    switch (type) {
      case 'deposit':
        return 'ðŸ“¥';
      case 'withdrawal':
        return 'ðŸ“¤';
      case 'transfer':
        return 'ðŸ”„';
      default:
        return 'ðŸ’³';
    }
  };

  const formatAmount = (amount: number, type: string) => {
    const prefix = type === 'withdrawal' ? '-' : '+';
    return `${prefix}$${amount.toFixed(2)}`;
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatTransactionType = (type: string) => {
    return type.charAt(0).toUpperCase() + type.slice(1);
  };

  return (
    <TransactionsContainer>
      <PageTitle>Transaction History</PageTitle>

      <FiltersSection>
        <Card elevation="low">
          <h3 style={{ marginBottom: '1rem', color: '#374151' }}>Filters</h3>
          <FiltersGrid>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontSize: '0.875rem', fontWeight: '500', color: '#4b5563' }}>
                Wallet
              </label>
              <FilterSelect
                value={selectedWallet}
                onChange={(e) => setSelectedWallet(e.target.value)}
              >
                <option value="all">All Wallets</option>
                {wallets.map((wallet) => (
                  <option key={wallet.id} value={wallet.id}>
                    {wallet.currency} Wallet
                  </option>
                ))}
              </FilterSelect>
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontSize: '0.875rem', fontWeight: '500', color: '#4b5563' }}>
                Type
              </label>
              <FilterSelect
                value={selectedType}
                onChange={(e) => setSelectedType(e.target.value)}
              >
                <option value="all">All Types</option>
                <option value="deposit">Deposits</option>
                <option value="withdrawal">Withdrawals</option>
                <option value="transfer">Transfers</option>
              </FilterSelect>
            </div>

            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontSize: '0.875rem', fontWeight: '500', color: '#4b5563' }}>
                Date Range
              </label>
              <FilterSelect
                value={dateFilter}
                onChange={(e) => setDateFilter(e.target.value)}
              >
                <option value="all">All Time</option>
                <option value="today">Today</option>
                <option value="week">This Week</option>
                <option value="month">This Month</option>
              </FilterSelect>
            </div>
          </FiltersGrid>
          
          <div style={{ display: 'flex', gap: '1rem', marginTop: '1rem' }}>
            <Button variant="outline" onClick={() => {
              setSelectedWallet('all');
              setSelectedType('all');
              setDateFilter('all');
            }}>
              Clear Filters
            </Button>
            <Button variant="primary">
              Export CSV
            </Button>
          </div>
        </Card>
      </FiltersSection>

      <TransactionsList>
        {transactions.length > 0 ? (
          transactions.map((transaction) => (
            <TransactionItem key={transaction.id} elevation="low">
              <TransactionInfo>
                <TransactionIcon type={transaction.type}>
                  {getTransactionIcon(transaction.type)}
                </TransactionIcon>
                <TransactionDetails>
                  <TransactionType>
                    {formatTransactionType(transaction.type)}
                  </TransactionType>
                  <TransactionDescription>
                    {transaction.description}
                  </TransactionDescription>
                </TransactionDetails>
              </TransactionInfo>
              <div style={{ textAlign: 'right' }}>
                <TransactionAmount type={transaction.type}>
                  {formatAmount(transaction.amount, transaction.type)}
                </TransactionAmount>
                <TransactionDate>
                  {formatDate(transaction.date)}
                </TransactionDate>
              </div>
            </TransactionItem>
          ))
        ) : (
          <Card elevation="low">
            <EmptyState>
              <EmptyStateIcon>ðŸ“Š</EmptyStateIcon>
              <EmptyStateTitle>No transactions found</EmptyStateTitle>
              <EmptyStateDescription>
                You haven't made any transactions yet. Start by making a deposit or transfer.
              </EmptyStateDescription>
              <Button variant="primary">
                Make Your First Transaction
              </Button>
            </EmptyState>
          </Card>
        )}
      </TransactionsList>
    </TransactionsContainer>
  );
};

export default TransactionsPage;
