import React, { useState } from 'react';
import styled from 'styled-components';
import Button from './Button';
import Input from './Input';
import Card from './Card';

// Modal backdrop
const ModalBackdrop = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 1rem;
`;

// Modal container
const ModalContainer = styled(Card)`
  width: 100%;
  max-width: 500px;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
`;

// Modal header
const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1.5rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid #e5e7eb;
`;

const ModalTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #1f2937;
  margin: 0;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #6b7280;
  padding: 0.25rem;
  border-radius: 0.25rem;
  
  &:hover {
    background-color: #f3f4f6;
    color: #374151;
  }
`;

// Form section
const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  font-size: 0.875rem;
  font-weight: 500;
  color: #4b5563;
  margin-bottom: 0.25rem;
`;

const Select = styled.select`
  padding: 0.5rem 0.75rem;
  font-size: 1rem;
  border-radius: 0.375rem;
  border: 1px solid #d1d5db;
  background-color: white;
  color: #1f2937;
  
  &:focus {
    outline: none;
    border-color: #3b82f6;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.2);
  }
`;

// Action buttons
const ActionButtons = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 1.5rem;
  padding-top: 1rem;
  border-top: 1px solid #e5e7eb;
`;

interface TransactionModalProps {
  walletId: string;
  type: 'deposit' | 'withdraw' | 'transfer';
  onClose: () => void;
}

const TransactionModal: React.FC<TransactionModalProps> = ({
  walletId,
  type,
  onClose
}) => {
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [recipientWallet, setRecipientWallet] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const getModalTitle = () => {
    switch (type) {
      case 'deposit':
        return 'Deposit Money';
      case 'withdraw':
        return 'Withdraw Money';
      case 'transfer':
        return 'Transfer Money';
      default:
        return 'Transaction';
    }
  };

  const getSubmitButtonText = () => {
    switch (type) {
      case 'deposit':
        return 'Deposit';
      case 'withdraw':
        return 'Withdraw';
      case 'transfer':
        return 'Transfer';
      default:
        return 'Submit';
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!amount || parseFloat(amount) <= 0) {
      alert('Please enter a valid amount');
      return;
    }

    if (type === 'transfer' && !recipientWallet) {
      alert('Please select a recipient wallet');
      return;
    }

    setIsLoading(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      // Here you would make the actual API call
      console.log('Transaction data:', {
        walletId,
        type,
        amount: parseFloat(amount),
        description,
        recipientWallet: type === 'transfer' ? recipientWallet : undefined
      });

      alert(`${getSubmitButtonText()} successful!`);
      onClose();
    } catch (error) {
      alert('Transaction failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  return (
    <ModalBackdrop onClick={handleBackdropClick}>
      <ModalContainer elevation="high">
        <ModalHeader>
          <ModalTitle>{getModalTitle()}</ModalTitle>
          <CloseButton onClick={onClose}>×</CloseButton>
        </ModalHeader>

        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label htmlFor="amount">Amount ($)</Label>
            <Input
              id="amount"
              type="number"
              placeholder="0.00"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              min="0"
              step="0.01"
              required
              fullWidth
            />
          </FormGroup>

          {type === 'transfer' && (
            <FormGroup>
              <Label htmlFor="recipientWallet">Recipient Wallet</Label>
              <Select
                id="recipientWallet"
                value={recipientWallet}
                onChange={(e) => setRecipientWallet(e.target.value)}
                required
              >
                <option value="">Select recipient wallet</option>
                <option value="wallet1">USD Wallet - user@example.com</option>
                <option value="wallet2">EUR Wallet - john@example.com</option>
                <option value="wallet3">GBP Wallet - jane@example.com</option>
              </Select>
            </FormGroup>
          )}

          <FormGroup>
            <Label htmlFor="description">Description (Optional)</Label>
            <Input
              id="description"
              type="text"
              placeholder={`Enter ${type} description...`}
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              fullWidth
            />
          </FormGroup>

          <ActionButtons>
            <Button
              type="button"
              variant="outline"
              onClick={onClose}
              fullWidth
              disabled={isLoading}
            >
              Cancel
            </Button>
            <Button
              type="submit"
              variant="primary"
              isLoading={isLoading}
              fullWidth
            >
              {getSubmitButtonText()}
            </Button>
          </ActionButtons>
        </Form>
      </ModalContainer>
    </ModalBackdrop>
  );
};

export default TransactionModal;
